package coding21;

public class Left_Right_Shift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 5;
		System.out.println(n<<2); // left shift o/p-> 20
		int n1 = -5;
		System.out.println(n1<<2); // o/p-> -20
		int m = 17;
		System.out.println(m>>2); // right shift  o/p -> 4
		int m1 = -17;
		System.out.println(m1>>2); // o/p -> -5

	}

}
